import React from 'react'
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Grid from '@material-ui/core/Grid'

const useStyles = makeStyles((theme: Theme)=>
  createStyles({
    container: {
      backgroundColor: "rgb(33, 117, 46)",
      height: "25vh",
      color: "rgb(135, 237, 144)",
      textAlign: "center"
    },
    header: {
      margin: 0,
      padding: 20
    }
  }))


export const Topics = () => {
  const classes = useStyles();
  return (
    <div className={classes.container}>
      <h2 className={classes.header}>Topics we are interested in covering (send us a pitch!)</h2>
      <Grid item xs>
        Environmental impact of business
        Politics of green technology
        History behind key industries
      </Grid>
      <Grid item xs>
        Green technology
        Clean energy
        Climate change
      </Grid>
      <Grid item xs>
        How to invest in green technology
        Where green technology is headed
        Companies to watch
      </Grid>
    </div>
  )
}
