import React from "react";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import GreenlyLogo from "../../assets/Greenly_logo.png";
import { TextField, Typography } from "@material-ui/core";
import Background from "../../assets/BackgroundWithClouds.png";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    container: {
      fontFamily: "Nunito",
      textAlign: "center",
      justifyContent: "center",
      color: "rgb(0, 107, 56)",
      height: "65vh",
      backgroundImage: `url(${Background})`,
      padding: 0,
      margin: 0,
      width: "100%",
    },
    comingSoon: {
      color: "rgb(0, 224, 140)",
    },
    logo: {},

    textBody: {
      marginRight: "30%",
      marginLeft: "30%",
      alignSelf: "center",
      justifySelf: "center",
      fontFamily: "Nunito Sans",
      fontSize: "26px"
    },
    form: {
      padding: 0,
    },
    emailSubmit: {
      width: "40vw",
      marginRight: "20%",
      marginLeft: "20%",
      padding: 0,
      margin: 0,
    },
  })
);

export const Main = () => {
  const classes = useStyles();
  return (
    <div className={classes.container}>
      <Typography>
        <br />
        <p className={classes.comingSoon}>COMING SOON...</p>
        <img src={GreenlyLogo} alt="logo" className={classes.logo} />
        <br />
        <br />
        <Typography className={classes.textBody} align="center">
          For centuries, our fossil fuel civilization has pushed the world off
          balance.
          <br />
          We're here to steer our planet back towards a more sustainable
          equilibrium and, ultimately, a Green Revolution - the next step in
          humanity's progress towards a future unconstrained by carbon emissions
          and their ecological consequences.
        </Typography>
        <h2>Join us and fight for our planet</h2>
        <br />
        <form className={classes.form}>
          <TextField
            className={classes.emailSubmit}
            variant="filled"
            label="Enter your email to get an invitation to our facebook group"
            color="primary"
            InputProps={{ endAdornment: <SubmitButton /> }}
            margin="none"
          ></TextField>
        </form>
      </Typography>
    </div>
  );
};

const SubmitButton = () => {
  return (
    <Button
      style={{
        height: "100%"
      }}
      disableElevation
    >
      Submit
    </Button>
  );
};
