import React, { useState } from "react";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Grid from "@material-ui/core/Grid";
interface ContactModalProps {
  open: boolean;
  clickOpen: () => void;
  clickClose: () => void;
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    dialog: {
      padding: "100px"
    }
  }))

export const ContactModal = (props: ContactModalProps) => {
  const classes = useStyles();
  return (
    <Dialog className={classes.dialog} open={props.open} onEscapeKeyDown={props.clickClose} onBackdropClick={props.clickClose}>
      <DialogTitle>Thank you for joing the greenly community</DialogTitle>
      <Grid container>
        <Grid item xs={6}>
          <TextField label="First" />
        </Grid>
        <Grid item xs={6}>
          <TextField label="Last" />
        </Grid>
      </Grid>
      <TextField label="Email" />
      {/* dropdown with reason for reaching out */}
      <TextField label="Your Message Here" />
      <Button>Submit</Button>
    </Dialog>
  );
};
