import React, { useState } from "react";
import {
  makeStyles,
  Theme,
  createStyles,
  withStyles,
} from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogContentText from "@material-ui/core/DialogContentText";
import DialogTitle from "@material-ui/core/DialogTitle";
import Grid from "@material-ui/core/Grid";
import { MenuItem, Select } from "@material-ui/core";
import InputBase from "@material-ui/core/InputBase";

interface ContactModalProps {
  open: boolean;
  clickOpen: () => void;
  clickClose: () => void;
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    dialog: {
      padding: "10px",
    },
    fullWidthField: {
      width: "100%",
    },
    gridContainer: {
      paddingBottom: "10px",
    },
  })
);

const BootstrapInput = withStyles((theme: Theme) =>
  createStyles({
    root: {
      "label + &": {
        marginTop: theme.spacing(3),
      },
    },
    input: {
      borderRadius: 4,
      position: "relative",
      backgroundColor: theme.palette.background.paper,
      border: "1px solid #ced4da",
      fontSize: 16,
      padding: "10px 26px 10px 12px",
      transition: theme.transitions.create(["border-color", "box-shadow"]),
      // Use the system font instead of the default Roboto font.
      fontFamily: [
        "-apple-system",
        "BlinkMacSystemFont",
        '"Segoe UI"',
        "Roboto",
        '"Helvetica Neue"',
        "Arial",
        "sans-serif",
        '"Apple Color Emoji"',
        '"Segoe UI Emoji"',
        '"Segoe UI Symbol"',
      ].join(","),
      "&:focus": {
        borderRadius: 4,
        borderColor: "#80bdff",
        boxShadow: "0 0 0 0.2rem rgba(0,123,255,.25)",
      },
    },
  })
)(InputBase);

export const ContactModal = (props: ContactModalProps) => {
  const classes = useStyles();
  const [reason, setReason] = useState('');
  const handleSelectChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    setReason(event.target.value as string)
  }

  return (
    <Dialog
      open={props.open}
      onEscapeKeyDown={props.clickClose}
      onBackdropClick={props.clickClose}
    >
      <div className={classes.dialog}>
        <DialogTitle>Thank you for joing the greenly community</DialogTitle>
        <Grid container className={classes.gridContainer} alignItems="center">
          <Grid item xs={6}>
            <TextField label="First" variant="outlined" size="small" />
          </Grid>
          <Grid item xs={6}>
            <TextField label="Last" variant="outlined" size="small" />
          </Grid>
        </Grid>

        <Grid container className={classes.gridContainer}>
          <TextField
            label="Email"
            className={classes.fullWidthField}
            variant="outlined"
            size="small"
          />
        </Grid>
        <Grid container className={classes.gridContainer}>
          <Select
            className={classes.fullWidthField}
            variant="outlined"
            value={reason}
            onChange={handleSelectChange}
            input={<BootstrapInput />}
          >
            <MenuItem>Reason for reaching out...</MenuItem>
            <MenuItem>Contributor</MenuItem>
            <MenuItem>Keep me posted</MenuItem>
          </Select>
        </Grid>
        {/* dropdown with reason for reaching out */}
        <Grid container className={classes.gridContainer}>
          <TextField
            label="Your Message Here"
            multiline
            rows={8}
            className={classes.fullWidthField}
            variant="outlined"
            size="small"
          />
        </Grid>
      </div>
      <Button>Submit</Button>
    </Dialog>
  );
};
